int N = {cursor};
int arr[N];
static int count_prime = 0;
static vector<int> vec_prime(0);

// リストにNまで入れる
for (int i = 0; i < N; i++) {
	arr[i] = 1;
}
// 倍数を振り落としていく
for (int i = 2; i < sqrt(N); i++) {
	if (arr[i]) {
		for (int j = 0; i * (j + 2) < N; j++) {
			arr[i * (j + 2)] = 0;
		}
	}
}
// 残ったものを調べる
for (int i = 2; i < N; i++) {
	if (arr[i]) {
		vec_prime.push_back(i);
		count_prime++;
	}
}
